import React,{useState} from "react";

export default function Nav(props) {
const [text,settext] = useState("my name i ayu");


//logic for uppercase
function upperhandle() {
  
  settext( text.toUpperCase());
  console.log("object");
}

//
function changing(event) {
  settext(event.target.value);
  console.log("2")
}


  return (
    <>
      

      <section className=" bg-slate-200 w-full ">
      <div className="">
        <div className="flex flex-row justify-between px-4 py-2  bg-slate-300 ">
          <div className=""><i class="fa-solid fa-bars"></i>  </div>
          <div className="lgo">yourtext</div>
          <div className="about"><i class="fa-solid fa-circle-info"></i></div>
        </div>

      </div>
        
        <h1 className="font-bold text-2xl text-center">Enter your text</h1>
        <div className="text-center">
        <textarea onChange={changing}  value={text} name="ak" id="p1" cols="30" rows="10" className="text-red-200  w-10/12 pl-3 mt-3  rounded-xl outline-2 outline-red-300 border-2 border-red-900"></textarea>
        </div>
       
        <div>
    <nav>
        <div onClick={upperhandle} className="btn mt-2 flex flex-col space-y-4 md:space-y-0  md:flex-row justify-center sm:space-x-0 md:space-x-1">
        <div className="flex flex-row  md:space-x-1  ">
          <button className="button1 text-xl font-semibold mx-5 text-gray-100   p-2  w-1/2 bg-gray-600 hover:bg-gray-700 rounded-2xl">
            {props.first}
          </button>
          <button className="button2 text-xl font-semibold mr-5 text-gray-100 mx-auto p-2  w-1/2 bg-gray-600 hover:bg-gray-700 rounded-2xl ">
            {props.second}
          </button>
        </div>
        <div className="flex flex-row  md:space-x-1  ">
          <button className="button1 text-xl font-semibold mx-5 text-gray-100  p-2  w-1/2 bg-gray-600 hover:bg-gray-700 rounded-2xl">
            {props.first}
          </button>
          <button className="button2 text-xl font-semibold mr-5 text-gray-100 mx-auto p-2  w-1/2 bg-gray-600 hover:bg-gray-700 rounded-2xl ">
            {props.second}
          </button>
        </div>
        <div className="flex flex-row  md:space-x-1  ">
          <button className="button1 text-xl font-semibold mx-5 text-gray-100  p-2  w-1/2 bg-gray-600 hover:bg-gray-700 rounded-2xl">
            {props.first}
          </button>
          <button className="button2 text-xl font-semibold mr-5 text-gray-100 mx-auto p-2  w-1/2 bg-gray-600 hover:bg-gray-700 rounded-2xl ">
            {props.second}
          </button>
        </div>
      </div>
      </nav>
    </div>
        
      </section>





      
    </>
  );
}
